import { useEffect, useState } from 'react'
import { genreids } from './MovieList'
const MovieFavourites = () => {
  const [favourites, setfavourite] = useState([])
  const [genres, setGenres] = useState([])
  const [selectedGenreid, setSelectedGenreId] = useState();
  const [filteredFavourites,setFilteredFavourites]=useState([])
  useEffect(() => {
    const favouritesData = JSON.parse(
      localStorage.getItem('favourites') || '[]'
    )
    const genresData = favouritesData.map(data => data.genre_ids[0])
    setGenres(Array.from(new Set(genresData)))
    setfavourite(favouritesData)
    setFilteredFavourites(favouritesData)
  }, [])
  console.log(favourites)
  const handleGenreSelection=(e)=>
  {
    const id=e.target.dataset.id;
    setSelectedGenreId(id);

  }
  useEffect(()=>
  {
setfavourite((prevstate)=>
{
    return prevstate.filter(movie=>movie.genre_ids==selectedGenreid)

})
  },[selectedGenreid])

  return (
    <div>
      <h1>Favourites Movie</h1>
      <div className='favourite-wrapper'>
        <div className='left-section'>
          <div className='genre-wrapper'>
            <div
              className={`genre ${selectedGenreid === "" ? "selected" : ""}`}
              data-id=""
              onClick={handleGenreSelection}
            >
              All genre
            </div>
            {genres.map((genreId) => (
              <div
                className={`genre ${selectedGenreid == genreId ? "selected" : ""
            }`}
            data-id={genreId}
                onClick={handleGenreSelection}

             >
                {genreids[genreId]}
              </div>
            ))}
          </div>
        </div>

        <div className='right-section'>
          <table>
            <thead>
              <tr>
                <th>Image</th>
                <th>Title</th>
                <th>Genre</th>
                <th>popularity</th>
                <th>Rating</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {favourites.map(favourite => (
                <tr>
                  <th>
                    <img
                      src={`https://image.tmdb.org/t/p/w500/${favourite.backdrop_path}`}
                      style={{ width: '80px' }}
                    ></img>
                  </th>
                  <th>{favourite.title}</th>
                  <th>{genreids[favourite.genre_ids[0]]}</th>
                  <th>{favourite.popularity}</th>
                  <th>{favourite.vote_average}</th>
                  <th>
                    <button>delete</button>
                  </th>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}
export default MovieFavourites
